              $.ajax({
                url: '/scripts/site/ajax/update_geoip_zip_code.php',  // AJAX location
                type: 'POST',
                data: {
                  zip_code: _zip_code,  // Passed in zipcode
                  product_id: 99677, // Product id
                  product_shipping_promo_id: 5    // Product shipping promotion id
                },
                cache: false,
                dataType: 'html',

                // If the AJAX request was successful:
                success: function(json){
                  var response = jQuery.parseJSON(json);
                  if(response && response.success) {
                    displayWarrantiesForZipcode(_zip_code);
                    $('.error').html('&nbsp;');
                    $('#geoIP').html(response.data.shipping_html);


                    $('#availability_desc')
                      .removeClass()
                      .addClass(response.data.availability.className)
                      .text(response.data.availability.text);

                    $("#dialog_zipcode_update").dialog('close');
                  }
                  else {
                    handle_zip_error();
                  }
                },

                // If the AJAX request was NOT successful (404error):
                error: handle_zip_error
              });

            });
          }
        }
